import React,{useState, useEffect} from 'react'
import Slider from 'react-slick'
import SingelEpisode from './SingelEpisode'
function Episodes(props){



const [e , sete] = useState([{data:[], next:[]}])
const [ClickedMoreEpi , setClickedMoreEpi ] = useState(false)
const [LoadingEpisode , setLoadingEpisode ] = useState(false)


const EPISODE_API = `https://kitsu.io/api/edge/anime/${props.num}/episodes`

const reade = (API)=>{
    fetch(API)
    .then((res)=>res.json())
    .then((data)=>{
            sete(prev=> ({
                data: data.data,
                next: data.links.next ,
                }))
            }) 

}

useEffect(()=>{
        reade(EPISODE_API)
    },[props.num])
    

    function ClickMe(A){
       setClickedMoreEpi(true)
    }
    var settings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 5,
        initialSlide: 0,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
              dots: true
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1,
              initialSlide: 1
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
        ]
      };

    useEffect(()=>{
             if(ClickedMoreEpi){
                fetch(e.next)
                .then((res)=>res.json())
                .then((data)=>{
                    sete((prev => ({...prev, data: [...prev.data ,...data.data], next:data.links.next}))) 
                })  
            .then(setLoadingEpisode(false))
             }else return 
                
      
    }, [ClickMe])
    



    return( !e.data ? null : <Slider {...settings}>
        {e.data.map((item)=>{
           return <SingelEpisode data={e}/>
        })}
    </Slider>)
   

}


export default Episodes
