import React from 'react'
import { Link } from 'react-router-dom';
import Slider from 'react-slick'
import ActorCard from './ActorCard';
import Stars from './Stars';



function TrendingCard(props){


    const data=props.data


    var settings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 5,
        initialSlide: 0,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
              dots: true
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1,
              initialSlide: 1
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
        ]
      };


    return(
        <div style={{padding: "2%"}}>
        <Slider {...settings}>
<ActorCard/>
    </Slider>
        </div>
       )
}

export default TrendingCard



