import React , {useState , useEffect} from 'react'
import Skeleton, { SkeletonTheme } from 'react-loading-skeleton'


function Character(props){

    

    const [Character , SetCharacter] = useState([])
    const SINGEL_CHARACTER_API = `https://kitsu.io/api/edge/media-characters/${props.id}/character`



    useEffect(()=>{ 

        fetch(SINGEL_CHARACTER_API)
        .then((res)=>res.json())
        .then((data)=>{
            SetCharacter(prev=> ({ ...data.data,...prev.data}))
            }) 
        }
        ,[])

        
        let img_url 

        if(!Character.attributes.image){
            img_url = "https://pm1.narvii.com/6529/68caee022fc10f36230f46eff8e6e4386f5e11e7_hq.jpg"
        }else{
        
            img_url = `${Character.attributes.image.original}`
        }

        let cardBackground={
            backgroundImage: "url(" + img_url + ")"  , height:"350px",
            backgroundSize:"cover", 
            margin : "0% 10px",
            backgroundRepeat:"no-repeat" }

    return(
    <div>  
         {Character.id ? 

            <div className="card" style={cardBackground}>
        <h3>{Character.attributes.canonicalName}</h3>
        {Character.attributes.image ?  <img alt={Character.attributes.canonicalName} src={Character.attributes.image.original}/> : <img alt="no found" src="https://static.zerochan.net/Fredrika.%28Hitsugi.no.Chaika%29.full.1850904.jpg"/>  }
        
        </div>
         
         
         : null }
    </div>
    )
}

export default Character
