import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { useTransition } from 'react-spring'
import { animated } from 'react-spring/renderprops-universal'
import SearchAnime from './SearchAnime'
import HomeSvg from './SVG/HomeSVG'
import MovieSVG from './SVG/MovieSVG'
import SearchSVG from './SVG/SearchSVG'

function NavBar(props){

const[show,setShow]=useState(false)


const transitions = useTransition(show, null , {
    from: { opacity: 0, transform: 'translate3d(100%,0,0)' },
    enter: { opacity: 1, transform: 'translate3d(0%,0,0)' },
    leave: { opacity: 0, transform: 'translate3d(-50%,0,0)' },
  })


function ShowSearchBar(props){
    console.log(show)   
     return setShow(!show)
}

   return(
       <div > 
    {transitions.map(({ item, key, props }) => item && <animated.div key={key} style={props}>
<SearchAnime/>
</animated.div>
)}
       <div className="navBar" >
    <div className="navBar-item">
    <Link to="/"><HomeSvg></HomeSvg></Link> 
    <Link to="/movies"><MovieSVG/></Link>
    <div onClick={ ShowSearchBar}>
    <SearchSVG/>
    </div>
    </div>  
    </div>
     </div>)
}
export default NavBar
