import React from 'react'

function Card(props){

    const maxLenght = 247

    const style = {
        position: "relative"
    }
const descriptionStyle ={
    position: "absolute" ,
    bottom:"0%",
    background:"blue"
}

const moreInfo = {
    position:"absolute" ,
    top : "20%"
}

    return(
    <div style={style} >
    <h2>{props.name}</h2>
    <img src={props.img} alt="Anime Poster"/>
    <div style = {descriptionStyle}>
    <button style = {moreInfo}>"More Info"</button>
    {props.description.length > maxLenght ? props.description.substring(0, maxLenght) :  props.description}
    </div>
    </div>

    )
}

export default Card
