import React from 'react'
import Slider from 'react-slick'



function TrendingCard(props){

    const style = {
        position: "relative" ,
        height:"450px",
        backgroundSize:"cover",
        backgroundRepeat:"no-repeat",
    }

    console.log(props.data)   


    const settings = {
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 3
      }

    return(
        <div >
        <Slider {...settings}>
 {props.data.map((e)=>{
     
        return <div  key={e.id}>
        <div>
        <h1 style={{backgroundImage: "url(" + e.attributes.posterImage.small + ")"}}>{e.attributes.canonicalTitle}</h1>
        </div>
       
        </div>
    })}
    </Slider>
        </div>
       )
}

export default TrendingCard
