import React, { useState, useEffect } from "react";
import Card from "./cards";

const Fetch = () => {
  const [data, setData] = useState({
      data : "",
      links : "",
      meta : ""
  });
  const [isLoading , setIsLoading] = useState(true)

  const dataFetch = (e)=> {
    const url = 'https://kitsu.io/api/edge/anime'
    setTimeout(()=>fetch(url)
      .then(response => response.json())
      .then(json => {        
          setData(json)
        setIsLoading(false)        
      }),1000)
  }




  console.log(data)
  return(
  isLoading ? 'Loading...' : 
  data.data.map(e=>
    <Card id={e.id} name={e.attributes.canonicalTitle} img={e.attributes.posterImage.small} description={e.attributes.description}/>
   
      )
     
  )};

export default Fetch;
