import React , {useState , useEffect} from 'react'

function ActorCard(props){

    

    const [Actor , SetActor] = useState([])
    const SINGEL_ACTOR_API = `https://kitsu.io/api/edge/media-staff/${props.id}/person`


    useEffect(()=>{ 

        fetch(SINGEL_ACTOR_API)
        .then((res)=>res.json())
        .then((data)=>{
            SetActor(prev=> ([data.data]))
            }) 
        }
        ,[])

        var settings = {
            dots: true,
            infinite: false,
            speed: 500,
            slidesToShow: 5,
            slidesToScroll: 5,
            initialSlide: 0,
            responsive: [
              {
                breakpoint: 1024,
                settings: {
                  slidesToShow: 3,
                  slidesToScroll: 3,
                  infinite: true,
                  dots: true
                }
              },
              {
                breakpoint: 600,
                settings: {
                  slidesToShow: 1,
                  slidesToScroll: 1,
                  initialSlide: 1
                }
              },
              {
                breakpoint: 480,
                settings: {
                  slidesToShow: 1,
                  slidesToScroll: 1
                }
              }
            ]
          };
    
    


    return(<div style={{padding:"2%" , display:"grid" , gridTemplateColumns:"1fr 1fr 1fr"}}>
        {!Actor ? "...Loading": Actor.map((e)=>{
   return <div  key={e.id}>
        {e.attributes.name}
{e.attributes.image ? <img style={{width:"250px" ,height:"250px"}}src={e.attributes.image.original}/> :<img style={{width:"250px" ,height:"250px"}} src="https://www.kindpng.com/picc/m/52-526798_anonymous-person-transparent-hd-png-download.png"/>}
    </div>
    }) } 
    
    </div>)
}

export default ActorCard
