import React from 'react'

function Card(props){

    const maxLenght = 247

    const style = {
        position: "relative"
    }

const moreInfo = {
    position:"absolute" ,
    bottom : "4%" ,
    left: "33.3%"

}

    return(
    <div onScroll={props.onScroll} style={style} > 
    <h2>{props.name}</h2>
    <img src={props.img} alt="Anime Poster"/>
    <div className="card-description">
    {props.description.length > maxLenght ? props.description.substring(0, maxLenght) :  props.description}
    <button style = {moreInfo}>"More Info"</button>
    </div>
    </div>

    )
}

export default Card
