import React,{useState , useEffect} from 'react'
import Slider from 'react-slick'
import Character from './character'
import {LoadMore} from './LoadFunctions'
import {ReadData} from './ReadFunction'
function Characters(props){


    const [Data, setData] = useState([{data:[], next:[]}])

    
    const API = `https://kitsu.io/api/edge/anime/${props.num}/characters`


     
    var settings = {
        dots: true,
        infinite: false,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 5,
        initialSlide: 0,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3,
              infinite: true,
              dots: true
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1,
              initialSlide: 1
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1
            }
          }
        ]
      };




useEffect(()=>{
       ReadData(API)
    },[props.num])

        
    return !Data.data ? "Loading" :
    <Slider {...settings}>
{ Data.data.map((e)=>{
       return <div  key={e.id}> 
        <Character id={e.id}/>
   </div>
    })}
    <button onClick={LoadMore}>LoadMoRE</button>
    </Slider>
}

export default Characters
