import React , {useState , useEffect} from 'react'

function ActorCard(props){


    const [Actor , SetActor] = useState([])
    const SINGEL_ACTOR_API = `https://kitsu.io/api/edge/media-staff/${props.id}/person`


    useEffect(()=>{ 

        fetch(SINGEL_ACTOR_API)
        .then((res)=>res.json())
        .then((data)=>{
            SetActor(prev=> ([data.data]))
            }) 
        }
           // eslint-disable-next-line react-hooks/exhaustive-deps
        ,[])

    

    return(!Actor ? "...Loading": Actor.map((e)=>{
   return <div  style={{backgroundImage: "url(" + e.attributes.posterImage.small ?  e.attributes.posterImage.small  : null  + ")" , position: "relative" ,
   height:"450px",
   backgroundSize:"cover", 
   margin : "0% 10px",
   backgroundRepeat:"no-repeat",}} style={{position:"relative"}} key={e.id}>
      <h3>{e.attributes.name}</h3>
      <p>Role:{props.role}</p>
    </div>
    }) 
    
    )
}

export default ActorCard
