import React ,{useState , useEffect} from 'react'

function SearchAnime(props){

    const [query , setquery] = useState('')
    
    const SEARCH_API = `https://kitsu.io/api/edge/anime?filter[text]=$`
    function quary(e){
       setquery((prev)=>{
           setquery(query)
       })
       console.log(quary)

    }

    const style={
        fontSize: "1.7rem", 
        textAlign: "center" ,
        width:"100%" ,
        borderRadius:"25px",
        maxWidth:"100%",
        background:"black",
        color:"white",
        padding: "20px 0"

    }
        

    return <div style={{textAlign: "center" , position:"fixed" , zIndex:"1" , paddingLeft:"80px" , width:"100%"}}>     
    <input style={style} type="text" name="name" placeholder="Search..." onChange={quary} />
            </div>   
        
    
}


export default SearchAnime
