import React, {useState , useEffect} from 'react'
import ActorCard from './ActorCard'

function AllActors(props){
const [data , setData]= useState([])

const API = `https://kitsu.io/api/edge/anime/${props.num}/anime-staff`

    useEffect(()=>{ 

        fetch(API)
        .then((res)=>res.json())
        .then((data)=>{
            setData(prev=> ({ ...data.data,...prev.data}))
            }) 
        }
        ,[])
console.log(data)
return !data ? console.log("error") : <ActorCard id={data.id}/>

}

export default AllActors
