import React, { useState, useEffect } from "react";
import Card from "./cards";

const Fetch = () => {
  const [data, setData] = useState();
  const [isLoading , setIsLoading] = useState(false)
  const [page , setPage] = useState(0)

  const fetchData = useEffect(()=>{
    const url = `https://kitsu.io/api/edge/anime?page[limit]=20&page[offset]=${page}`
    setTimeout(()=>fetch(url)
      .then(response => response.json()) 
      .then(json => {        
          setData(json)
        setIsLoading(true)        
      }),1000)
  
  },[])


  useEffect(()=>{
     const dataFetch = async ()=> {
         setIsLoading(true)
        
         const newAnime = await  fetchData(page)
         setData((prev)=>[...prev , ...newAnime ])
         console.log
         setIsLoading(true)
     }
     dataFetch()
  }
  )

    

  console.log(data)
  return(
  isLoading ? 'Loading...' : 
  data.data.map(e=>
    <Card id={e.id} name={e.attributes.canonicalTitle} img={e.attributes.posterImage.small} description={e.attributes.description}/>
   
      )
     
  )};

export default Fetch;
