import React,{useState, useEffect} from 'react'
import Card from './cards'
import SearchAnime from './SearchAnime'


function Movies(){

const [data , setData] = useState([])
const [isLoading , setIsLoading] = useState(true)
const [value , SetValue] = useState('')
const [isBottom, setIsBottom] = useState(true);
const [page , setPage] = useState(0)
const [show, setShow] = useState(false)
const [num , setNum]= useState(1)
const [singel , setSingel] = useState([])
const [e , sete] = useState([{data:[], next:[] , loaded: false}])



const LISTED_API= `https://kitsu.io/api/edge/anime?page[limit]=10&page[offset]=${page}`
const SEARCH_API = `https://kitsu.io/api/edge/anime?filter[text]=$`
const SINGEL_API = `https://kitsu.io/api/edge/anime?filter[id]=`
const EPISODE_API = `https://kitsu.io/api/edge/anime/${num}/episodes`
const Youtube_link = `https://www.youtube.com/embed/`

useEffect(() => {
    if (isBottom) { 
        if(value){
            return
        }else
        setPage((prev)=>prev + 20)
        readData(LISTED_API)
    }
  }, [isBottom]); 

//FEATCHING API DATA , SET DATA , Set Loading //

const readData = (API)=> {
    setIsLoading(true)
    fetch(API)
     .then((res)=>res.json())
     .then((data)=>{
         if(!value){
             if(page === 0){
                setData(prev => [...data.data])
             }if(page > 0){
                setData(prev => [...prev, ...data.data])         
             }     
         }else {
             setData((prev)=>[...data.data])
         }
 
       })
     .then(setIsLoading(false))
   
 }

 const readSingel = (API)=>{
     fetch(API)
     .then((res)=>res.json())
     .then((data)=>{
         setSingel((prev)=>[...data.data])    
     })
 
    
 }

useEffect(()=>{
readSingel(SINGEL_API+num)
},[num])



const reade = (API)=>{
    fetch(API)
    .then((res)=>res.json())
    .then((data)=>{
        if(!e.loaded){
            sete(prev=> ({ data: data.data, next: data.links.next , loaded:true }))    
        }else{
            sete(prev=> ({ data:[...prev.data, ...data.data], next: data.links.next , loaded:true }))    
        }
      
    })


}

useEffect(()=>{
   reade(EPISODE_API)
    },[moreE])
    
function moreE(e){
    return
}

function HideItemId(e){
    setNum(e.target.value)
    return setShow(true)  
}

function ShownItemId(){
    return setShow(false)  
}


//search handel//

function handelChange(e){
    const searchValue = e.target.value
        SetValue( searchValue)
        readData(SEARCH_API+searchValue)

        if (!searchValue){
        SetValue(null)
        setData([])
        setPage((prev)=>prev  * 0)
        readData(LISTED_API) 
        }

}
//Listen to Scroll //

useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);


//Set True if reach Buttom//

function handleScroll(){
        if ((window.innerHeight + window.scrollY) >= document.body.offsetHeight-100) {
            setIsBottom(true)
         }else{
             setIsBottom(false)
         }
}

//close singel if ESC got clicked

useEffect(() => {
    const close = (e) => {
      if(e.keyCode === 27){
       ShownItemId()
      }
    }
    window.addEventListener('keydown', close)
  return () => window.removeEventListener('keydown', close)
},[])



 //////////////////////////////////////





    
return(<div>

<header>
   
 <SearchAnime change={(e)=>{handelChange(e)}}/>

</header>

{show && singel[0].attributes.canonicalTitle ? 
<div className="centered">
<div>
<h1 >{singel[0].attributes.canonicalTitle}</h1>  
{singel[0].attributes.coverImage == null ?
 <img alt="anime Charcter in japan" src="https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/71b0a868-5bf0-4c2f-ba02-d7b6e81d78e6/d7t1lcx-f63e32f5-57af-431a-8f05-22d3d75a5414.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJ1cm46YXBwOiIsImlzcyI6InVybjphcHA6Iiwib2JqIjpbW3sicGF0aCI6IlwvZlwvNzFiMGE4NjgtNWJmMC00YzJmLWJhMDItZDdiNmU4MWQ3OGU2XC9kN3QxbGN4LWY2M2UzMmY1LTU3YWYtNDMxYS04ZjA1LTIyZDNkNzVhNTQxNC5qcGcifV1dLCJhdWQiOlsidXJuOnNlcnZpY2U6ZmlsZS5kb3dubG9hZCJdfQ.9uc1GVEcMeAefdNRe10bFuWH5p1jomvOgNk3GrKyjUQ"/>  
 : <img alt={singel[0].attributes.canonicalTitle} src={singel[0].attributes.coverImage.small}/> }
<p>{singel[0].attributes.description}</p>
{Youtube_link+singel[0].attributes.youtubeVideoId ? <iframe src= {Youtube_link+singel[0].attributes.youtubeVideoId}
        frameBorder='0'
        allow='autoplay; encrypted-media'
        title='video'
/>
 : null}
<div></div>
 
 <div className="grid-container">
 {e.data.map((e)=>{
    return <div key={e.id}> 
    {e.attributes.canonicalTitle ? <div> 
     <h1>Episode {+e.attributes.number}</h1>
     <h4 >{e.attributes.canonicalTitle}</h4>
     <p>{e.attributes.airdate}</p> 
     
     </div> : null}
</div>
 })}
 </div>
 <button >loadMore EPisodes</button>
  <div><button onClick={ShownItemId}>to Close Click here or Press "ESC"</button></div>

 
</div>


</div>

: null }

<div className="grid-container">

{isLoading ? 'Loading...' : data.map(e=>
      <Card 
      key={e.id}
     id={e.id}
    name={e.attributes.canonicalTitle}
    img={e.attributes.posterImage.small}
    description={e.attributes.description}
    showOn={ShownItemId} 
    showOff={HideItemId} 

    />)}

</div>


</div>)
}
    export default Movies
