import React, { useState, useEffect } from "react";
import Card from "./cards";
import {getData} from "./newfile"
const Fetch = () => {
  const [data, setData] = useState([]);
  const [isLoading , setIsLoading] = useState(true)
  const [page , setPage] = useState(0)


  useEffect(()=>{
     const dataFetch = async ()=> {
         setIsLoading(true)
        
         const newAnime = await  getData(page)
         setData((prev)=>[...prev , ...newAnime ])
         console.log
         setIsLoading(true)
     }
     dataFetch()
  },[])

    

  console.log(data)
  return(
  isLoading ? 'Loading...' : 
  data.data.map(e=>
    <Card id={e.id} name={e.attributes.canonicalTitle} img={e.attributes.posterImage.small} description={e.attributes.description}/>
   
      )
     
  )};

export default Fetch;
