         setIsLoading(false)
     }
     dataFetch()
  },[page])

    console.log(data)
  return(
  isLoading ? 'Loading...' : 
  data.map(e=>
    <div key={e.id}>
  <Card 
    id={e.id}
    name={e.attributes.canonicalTitle}
    img={e.attributes.posterImage.small} 
    description={e.attributes.description}/>
    </div>
  
   
      )
     
  )};

export default Fetch;
