import React,{useState, useEffect} from 'react'
import Slider from 'react-slick'
import SingelEpisode from './SingelEpisode'
function Episodes(props){



const [e , sete] = useState([{data:[], next:[]}])
const [ClickedMoreEpi , setClickedMoreEpi ] = useState(false)
const [LoadingEpisode , setLoadingEpisode ] = useState(false)


const EPISODE_API = `https://kitsu.io/api/edge/anime/${props.num}/episodes`

const reade = (API)=>{
    fetch(API)
    .then((res)=>res.json())
    .then((data)=>{
            sete(prev=> ({
                data: data.data,
                next: data.links.next ,
                }))
            }) 

}

useEffect(()=>{
        reade(EPISODE_API)
    },[props.num])
    console.log(e)

    function ClickMe(A){
        fetch(e.next)
        .then((res)=>res.json())
        .then((data)=>{
            sete((prev => ({...prev, data: [...prev.data ,...data.data], next:data.next}))) 
        })  
    }
    
    


    return( !e.data ? null : <SingelEpisode data={e} onClick={ClickMe}/>)
   

}


export default Episodes
