import React from 'react'

function Card(props){

    const maxLenght = 247

    const style = {
        position: "relative"
    }


    return(
    <div style={style} > 
    <h2>{props.name}</h2>
    <img src={props.img} alt="Anime Poster"/>
    <div className="card-description">
    {props.description}
    </div>
    </div>

    )
}

export default Card
