import React,{useState, useEffect} from 'react'
import Card from './cards'
import InfiniteScroll from 'react-infinite-scroller'

function Movies(){

const [data , setData] = useState([])
const [isLoading , setIsLoading] = useState(true)
const [page ,setPage] = useState(0)
const [value , SetValue] = useState('')

const style={
    fontSize: "2rem", 
    textAlign: "center" ,
    borderRadius:"25px",
    padding : "0% 20%" ,
    marginLeft:"19%"
}


const LISTED_API= `https://kitsu.io/api/edge/anime?page[limit]=20&page[offset]=${page}`
const SEARCH_API = `https://kitsu.io/api/edge/anime?filter[text]=$`




useEffect(()=>{
        readData(LISTED_API)    
    
},[])


const readData = (API)=> {
    fetch(API)
     .then((res)=>res.json())
     .then((data)=>{
         setData(data.data)
        })
     .then(setIsLoading(false))
 }




 const updateData = (API)=> {
    fetch(API)
     .then((res)=>res.json())
     .then((data)=>{
         
         setData((preValue)=>{
             console.log(page)
             setData([ ...preValue , data.data])
         })
        })
     .then(setIsLoading(false))
 }
 
    

function handelChange(e){
    const valuer = e.target.value
        SetValue(valuer)
        readData(SEARCH_API+valuer)

        if (!valuer){
            readData(LISTED_API)
        }

    
}
    

return(<div>

<header>

   <input style={style} type="text" name="name" placeholder="Search..." onChange={(e)=>{handelChange(e)}} />
   
   <h1>{value}</h1>

</header>



<InfiniteScroll
    pageStart={0}
    loadMore={(api)=>updateData(LISTED_API)}
    hasMore={true || false}
    loader={<div className="loader" key={0}>Loading ...</div>}
>
    {data.map(e=>
      <Card 
      key={e.id}
     id={e.id}
    name={e.attributes.canonicalTitle}
    img={e.attributes.posterImage.small}
    description={e.attributes.description}/>
     
        
       
    )} 
</InfiniteScroll>


<div className="grid-container">


</div>


</div>)
}
    export default Movies
