<div  className="card"  style={{backgroundImage: "url(" + Character.attributes.image.original + ")"  , height:"350px"}}>
<h3 >{Character.attributes.canonicalName}</h3>
</div>
  
        </div>
         : null }
    </div>
    )
}

export default Character
