import React,{useState, useEffect} from 'react'
import Card from './cards'
import Search from './SearchAnime'

const style={
    fontSize: "2rem", 
    textAlign: "center" ,
    borderRadius:"25px",
    padding : "0% 20%" ,
    marginLeft:"19%"
}


const LISTED_API= "https://kitsu.io/api/edge/trending/anime"
const SEARCH_API = `https://kitsu.io/api/edge/anime?filter[text]=${value}`

function Movies(){

const [data , setData] = useState([])
const [isLoading , setIsLoading] = useState(true)

const [value , SetValue] = useState('')






useEffect(()=>{
        readData(LISTED_API)
    
},[])


const readData = (API)=> {
    fetch(API)
     .then((res)=>res.json())
     .then((data)=>{
         setData(data.data)
         setIsLoading(false)
     })
 }
 


function handelChange(e){
    SetValue(e.target.value)  
    console.log(value)
    readData(SEARCH_API)

}



console.log(data)
return(<div>

<header>

   <input style={style} type="text" name="name" placeholder="Search..." onChange={handelChange} />

</header>

<div className="grid-container">
{isLoading ? 'Loading...' : data.map(e=>
      <Card 
      key={e.id}
     id={e.id}
    name={e.attributes.canonicalTitle}
    img={e.attributes.posterImage.small}
    description={e.attributes.description}/>
     
        
       
    )}

</div>


</div>)
}
    export default Movies
