import React from 'react'

function Card(props){

    const maxLenght = 247

    const style = {
        position: "relative"
    }

const moreInfo = {
    position:"absolute" ,
    bottom : "4%" ,
    left: "33.3%"

}




    return(
    <div value={props.id} style={style} >
   
        <h3>{props.name}</h3>
  
    <img src={props.img} alt="Anime Poster"/>
    <div className="card-description">
    {props.description.length > maxLenght ? props.description.slice(0, maxLenght): null}
    <button  value={props.id} onClick={props.showOff}  style = {moreInfo}>"More Info"</button>
    </div>
    </div>

    )
}

export default Card
