import React from 'react'

function Card(props){

const style={
    display:"grid" ,
    gridTemplateColumns: "auto auto auto auto"
}

    return(
    <div style={style}>
<div>
<h2>{props.name}</h2>
    <img src={props.img} alt="Anime Poster"/>
    <span>{props.description}</span>
</div>
 

    </div>

    )
}

export default Card
