import React from 'react'

function Card(props){

    const maxLenght = 100

    const style = {
        position: "relative"
    }
const descriptionStyle ={
    position: "absolute" ,
    top:"50%",
    background:"blue"
}

    return(
    <div style={style} >
    <h2>{props.name}</h2>
    <img src={props.img} alt="Anime Poster"/>
    <div style = {descriptionStyle}>
    <span>{props.description.length > maxLenght ? props.description.substring(0, maxLenght)  :  props.description}</span>
    </div>
  
    </div>

    )
}

export default Card
