import React, {useState , useEffect} from 'react'
import TrendingCar from './TrendingCard'

function TrendingAnime(){



    const [Tranding , setTranding] = useState([])
    const [Loading , SetLoading] = useState(false)
    const API = "https://kitsu.io/api/edge/trending/anime"

    const readTrending = (API)=>{
        fetch(API)
        .then((res)=>res.json())
        .then((data)=>{
            setTranding(prev=> (data.data))})
    }

    useEffect(()=>{
        readTrending(API)
        setTimeout(() => {
    SetLoading(true)
        }, 2000);
        console.log(Tranding)
        },[])

    return <div className="Trending-container" key="tEVELZZ">{Loading? 
    <div>
    <TrendingCar data={Tranding}/>
    </div>
    
     :
    
    <h1>No</h1>
    
    }</div>
}

export default TrendingAnime
