import React, { useState, useEffect } from "react";
import Card from "./cards";

const Fetch = () => {
  const [data, setData] = useState(null);
  const [isLoading , setIsLoading] = useState(true)

  const fetchData = useEffect(()=>{
    const url = 'https://kitsu.io/api/edge/trending/anime'
    setTimeout(()=>fetch(url)
      .then(response => response.json())
      .then(json => {        
          setData(json)
        setIsLoading(false)        
      }),1000)
  
  },[])

  const {datad , meta , links } = data


  let Checked = isLoading ? "loading..." :  console.log(meta)

  return(
  isLoading ? 'Loading...' : data.data.map(e=>
    <Card key={e.id} id={e.id} name={e.attributes.canonicalTitle} img={e.attributes.posterImage.small} description={e.attributes.description}/>
   
      )
     
  )};

export default Fetch;
