import React,{useState, useEffect} from 'react'


function SingelEpisode(props){



const [e , sete] = useState([{data:[], next:[]}])
const [ClickedMoreEpi , setClickedMoreEpi ] = useState(false)
const [LoadingEpisode , setLoadingEpisode ] = useState(false)


const EPISODE_API = `https://kitsu.io/api/edge/anime/${props.num}/episodes`

const reade = (API)=>{
    fetch(API)
    .then((res)=>res.json())
    .then((data)=>{
            sete(prev=> ({
                data: data.data,
                next: data.links.next ,
                }))
            }) 

}

useEffect(()=>{
        reade(EPISODE_API)
    },[props.num])
    

    function ClickMe(A){
       setClickedMoreEpi(true)
    }


    useEffect(()=>{
             if(ClickedMoreEpi){
                fetch(e.next)
                .then((res)=>res.json())
                .then((data)=>{
                    sete((prev => ({...prev, data: [...prev.data ,...data.data], next:data.links.next}))) 
                })  
            .then(setLoadingEpisode(false))
             }else return 
                
      
    }, [ClickMe])
    



    return( 
    <div>
    <Slider >
    {!e.data ? null :e.data.map((e)=>{
       return <div key={e.id}> 
       {e.attributes.canonicalTitle ? <div> 
        <h1>Episode {+e.attributes.number}</h1>
        <h4 >{e.attributes.canonicalTitle}</h4>
        <p>{e.attributes.airdate}</p> 
        <p>{e.attributes.description}</p> 
        </div> : 'This is a Movie /// missing episode name'}
   </div>
    })}
   
    </Slider>
    <button style={{padding:"2% 5%"}} onClick={(a)=>{ClickMe(a)}} value={props.cardNumber}>Load All episodes</button>
    </div>)
}


export default SingelEpisode
