import React from 'react'

function Card(props){

const gridContainer={
    display: "grid",
  gridTemplateColumns: "auto auto auto",
  backgroundColor:"#2196F3",
  padding: "10px", 
}

    return(
    <div style={gridContainer}>
    <h2>{props.name}</h2>
    <img src={props.img} alt="Anime Poster"/>
    <span>{props.description}</span>
    </div>

    )
}

export default Card
