import React,{useState, useEffect} from 'react'
import Card from './cards'


function Movies(){

const [data , setData] = useState([])
const [isLoading , setIsLoading] = useState(true)
const [value , SetValue] = useState('')
const [isBottom, setIsBottom] = useState(false);
const [page , setPage] = useState(0)

const style={
    fontSize: "2rem", 
    textAlign: "center" ,
    borderRadius:"25px",
    padding : "0% 20%" ,
    marginLeft:"19%"
}


const LISTED_API= `https://kitsu.io/api/edge/anime?page[limit]=20&page[offset]=${page}`
const SEARCH_API = `https://kitsu.io/api/edge/anime?filter[text]=$`




useEffect(()=>{
        readData(LISTED_API)    
    
},[])


const readData = (API)=> {
    fetch(API)
     .then((res)=>res.json())
     .then((data)=>{
         setData(data.data)
        })
     .then(setIsLoading(false))
 }

 
function handelChange(e){
    const valuer = e.target.value
        SetValue(valuer)
        readData(SEARCH_API+valuer)

        if (!valuer){
            readData(LISTED_API)
        }

    
}

//Listen to Scroll //
useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);



//Set True if reach Buttom//
function handleScroll(){
        if ((window.innerHeight + window.scrollY) >= document.body.offsetHeight) {
            setIsBottom(true)
         }else{
             setIsBottom(false)
         }
}

useEffect(() => {
    if (isBottom) { 
        setPage((prev)=>prev + 20)
        setData((prev)=>{

            return [...prev, data]
        })
    
    readData(LISTED_API) 
    }
  }, [isBottom]);


    

return(<div>

<header>

   <input style={style} type="text" name="name" placeholder="Search..." onChange={(e)=>{handelChange(e)}} />
   
   <h1>{value}</h1>

</header>

<div className="grid-container">

{isLoading ? 'Loading...' : data.map(e=>
      <Card 
      key={e.id}
     id={e.id}
    name={e.attributes.canonicalTitle}
    img={e.attributes.posterImage.small}
    description={e.attributes.description}/>     
       
    )}

</div>


</div>)
}
    export default Movies
