import React , {useState , useEffect} from 'react'
import MyFetch from './FetchFunction'

function Character(props){

    

    const [Character , SetCharacter] = useState([])
    const SINGEL_CHARACTER_API = `https://kitsu.io/api/edge/media-characters/${props.id}/character`



    useEffect(()=>{ 
       MyFetch(SINGEL_CHARACTER_API)
    },[])



    return(
    <div>  
         {Character.id ? 
         
        <div>
        <h3>{Character.attributes.canonicalName}</h3>
        {Character.attributes.image ?  <img src={Character.attributes.image.original}/> : <img src="https://static.zerochan.net/Fredrika.%28Hitsugi.no.Chaika%29.full.1850904.jpg"/>  }
        
        </div>
         
         
         : null}
    </div>
    )
}

export default Character
