import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { useSpring , animated, config, useTransition } from 'react-spring'
import { Spring } from 'react-spring/renderprops'


import SearchAnime from './SearchAnime'
import HomeSvg from './SVG/HomeSVG'
import MovieSVG from './SVG/MovieSVG'
import SearchSVG from './SVG/SearchSVG'

function NavBar(){


    const [on, toggle] = useState(false);
    
const transition = useTransition(on, null, {
    from: { transform: 'translate3d(0,-40px,0)' },
enter: { transform: 'translate3d(0,0px,0)' },
leave: { transform: 'translate3d(0,-40px,0)' },
  });



   return(
       <div > 
      {!on ? null : <div >  {transition.map(({ item, key, props }) => (
  item && <animated.div  style={props , {position: "relative", zIndex: "1"}} ><SearchAnime/></animated.div>
  ))}

 </div>}
       <div className="navBar" >
    <div className="navBar-item">
    <Link to="/"><HomeSvg></HomeSvg></Link> 
    <Link to="/movies"><MovieSVG/></Link>
    <div onClick={() => toggle(!on)}>
    <SearchSVG/>
    </div>
    </div>  
    </div>
     </div>)
}
export default NavBar
