import React,{useState, useEffect} from 'react'
import SingelEpisode from './Episodes'
import Character from './characters'
import { useHistory, useLocation } from 'react-router-dom';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";
import Skeleton, { SkeletonTheme } from 'react-loading-skeleton'
import { useTransition } from 'react-spring';
import { animated } from 'react-spring/renderprops-universal';

function SingelAnime(props){

  const location = useLocation()
  const transitions = useTransition(location, location => location.pathname, {
    from: { opacity: 0, transform: 'translate3d(100%,0,0)' },
    enter: { opacity: 1, transform: 'translate3d(0%,0,0)' },
    leave: { opacity: 0, transform: 'translate3d(-50%,0,0)' },
  })



    const [singel , setSingel] = useState([])
    const [isLoading , setIsLoading ]= useState([false])

    const SINGEL_API = `https://kitsu.io/api/edge/anime?filter[id]=`
    const Youtube_link = `https://www.youtube.com/embed/`

    const readSingel = (API)=>{
        setIsLoading(true)
        fetch(API)
        .then((res)=>res.json())
        .then((data)=>{
            setSingel((prev)=>[...data.data])    
        })
    }
   
   useEffect(()=>{
   readSingel(SINGEL_API+props.cardNumber)
   setTimeout(() => {
       setIsLoading(false)
   }, 1500);
   },[props.cardNumber])
   

   function Characterr() {
    return (
      <div>
      
      <h2>Characters</h2>
       <Character num={props.cardNumber}></Character>
      </div>
    );
  }
  
  function Episode() {
    return (
      <div>
      <h2>Episodes</h2>
        <SingelEpisode num={props.cardNumber}></SingelEpisode>
      </div>
    );
  }
  
  function Actors() {
    return (
      <div>
        <h2>Actors</h2>
      </div>
    );
  }


   const history = useHistory()
    return(
      !isLoading ?  <div> 

      <div className="Singel-Card">
      <div className="singel-card-info">  
        <h1>{singel[0].attributes.canonicalTitle }</h1>  
       <p>{singel[0].attributes.description}</p>
       </div>
      {singel[0].attributes.coverImage == null ?
       <img alt="anime Charcter in japan" src="https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/71b0a868-5bf0-4c2f-ba02-d7b6e81d78e6/d7t1lcx-f63e32f5-57af-431a-8f05-22d3d75a5414.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJ1cm46YXBwOiIsImlzcyI6InVybjphcHA6Iiwib2JqIjpbW3sicGF0aCI6IlwvZlwvNzFiMGE4NjgtNWJmMC00YzJmLWJhMDItZDdiNmU4MWQ3OGU2XC9kN3QxbGN4LWY2M2UzMmY1LTU3YWYtNDMxYS04ZjA1LTIyZDNkNzVhNTQxNC5qcGcifV1dLCJhdWQiOlsidXJuOnNlcnZpY2U6ZmlsZS5kb3dubG9hZCJdfQ.9uc1GVEcMeAefdNRe10bFuWH5p1jomvOgNk3GrKyjUQ"/>  
       : <img alt={singel[0].attributes.canonicalTitle} src={singel[0].attributes.posterImage.original}/> }
     
  

      </div> 
       


      <Router>
      <div style={{textAlign:"center"}} >
        <ul className="router-Grid">
          <li>
            <Link to={`${history.location.pathname}/Episode`}>Episode</Link>
          </li>
          <li>
            <Link to={`${history.location.pathname}/Characters`}>Character</Link>
          </li>
          <li>
            <Link to={`${history.location.pathname}/Actors`}>Actors</Link>
          </li>
        </ul>

        <hr />
        {transitions.map(({ item, props, key }) =>{
  return (
    <animated.div key={key} style={props}>
    <Switch location={item[0]}> 
          <Route exact path={`${history .location.pathname}/Episode`}>
            <Episode/>
          </Route>
          <Route path={`${history.location.pathname}/Characters`}>
            <Characterr/>
          </Route>
          <Route path={`${history.location.pathname}/Actors`}>
            <Actors/>
          </Route>
        </Switch>
    </animated.div>)})}
       
      </div>
    </Router>
       
      </div>  :
      
      <SkeletonTheme color="#202020" highlightColor="#444">
  <div className="Singel-Card">
  <div className="singel-card-info">
  <Skeleton  height="50px" width="50%" /> 
  <h1><Skeleton  height="350px" width="90%" /></h1>

</div>

  <Skeleton top="20%" height="700px" width="100%" /> 
  </div>
</SkeletonTheme>
      
  
    
   )
    
}

export default SingelAnime
