import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { useSpring , animated, config } from 'react-spring'


import SearchAnime from './SearchAnime'
import HomeSvg from './SVG/HomeSVG'
import MovieSVG from './SVG/MovieSVG'
import SearchSVG from './SVG/SearchSVG'

function NavBar(){

const[show,setShow]=useState(false)

const fadeStyles = useSpring({
    config: { ...config.stiff },
    from: { opacity: 0 },
    to: {
      opacity: 1
    }
  });



function ShowSearchBar(props){
    console.log(show)   
     return setShow(!show)
}

   return(
       <div > 
      {!show ? null : <div ><animated.div style={fadeStyles}><SearchAnime/></animated.div> </div>}
       <div className="navBar" >
    <div className="navBar-item">
    <Link to="/"><HomeSvg></HomeSvg></Link> 
    <Link to="/movies"><MovieSVG/></Link>
    <div onClick={ ShowSearchBar}>
    <SearchSVG/>
    </div>
    </div>  
    </div>
     </div>)
}
export default NavBar
