import React, { useState, useEffect } from "react";
import Card from "./cards";

const Fetch = () => {
  const [data, setData] = useState(null);
  const [isLoading , setIsLoading] = useState(true)

  useEffect(()=>{
    setTimeout(()=>fetch('https://kitsu.io/api/edge/trending/anime')
      .then(response => response.json())
      .then(json => {        
          setData(json)
        setIsLoading(false)        
      }),1000)
  
  },[0])



  



  return(
  isLoading ? 'Loading...' : data.data.map(e=><div>
    <Card id={e.attributes.canonicalTitle} name={e.Title} img={e.attributes.posterImage.small}/>
    {console.log(data.data)}
      </div>)
     
  )};

export default Fetch;
