import React ,{ useState , useEffect} from 'react'
import Card from './cards'

import SearchAnime from './SearchAnime'


function Next(){

    const [data , setData] = useState({
        loaded : false,
        page:0,
        data: []
    })
    const [isBottom, setIsBottom] = useState(true);


   const LISTED_API= `https://kitsu.io/api/edge/anime?page[limit]=10&page[offset]=${data.page}`


    useEffect(() => {
            readData(LISTED_API)
      }, [isBottom]); 

    const readData = (API)=>{
        fetch(API)
        .then((res)=>res.json())
        .then((data)=>{
            if(isBottom){
                setData((prev => ({...prev, data: data.data , loaded:true})))  
            }if(!isBottom){
                setData((prev => ({...prev, data: data.data , loaded:true , page:page+20})))  
            }
        })
    
    }

//Listen to Scroll //
    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
      }, []);
    
    
    //Set True if reach Buttom//
    
    function handleScroll(){
            if ((window.innerHeight + window.scrollY) >= document.body.offsetHeight-100) {
                setIsBottom(true)
             }else{
                 setIsBottom(false)
             }
    }
console.log(isBottom)
    return(
        <div>
        <SearchAnime/>
        <div className="grid-container">

{!data.loaded ? 'loading' : data.data.map(e=>
      <Card 
      key={e.id}
     id={e.id}
    name={e.attributes.canonicalTitle}
    img={e.attributes.posterImage.small}
    description={e.attributes.description}

    />)}

</div>
        </div>
    )
}

export default Next
