import React,{useState, useEffect} from 'react'
import Card from './cards'
import singelAnime from './singelAnime'
function Movies(){

const [data , setData] = useState([])
const [isLoading , setIsLoading] = useState(true)
const [value , SetValue] = useState('')
const [isBottom, setIsBottom] = useState(true);
const [page , setPage] = useState(0)
const [show, setShow] = useState(false)
const [num , setNum]= useState(1)
const [singel , setSingel] = useState([])
const [singelLoading , setSingelLoading]= useState(true)

const style={
    fontSize: "2rem", 
    textAlign: "center" ,
    borderRadius:"25px",
    padding : "0% 20%" ,
    marginLeft:"19%"
}


const LISTED_API= `https://kitsu.io/api/edge/anime?page[limit]=10&page[offset]=${page}`
const SEARCH_API = `https://kitsu.io/api/edge/anime?filter[text]=$`
const SINGEL_API = `https://kitsu.io/api/edge/anime?filter[id]=`

const Youtube_link = `https://www.youtube.com/embed/`

useEffect(() => {
    if (isBottom) { 
        if(value){
            return
        }else
        setPage((prev)=>prev + 20)
        readData(LISTED_API)
    }
  }, [isBottom]); 

//FEATCHING API DATA , SET DATA , Set Loading //
const readData = (API)=> {
    setIsLoading(true)
    fetch(API)
     .then((res)=>res.json())
     .then((data)=>{
         if(!value){
             if(page === 0){
                setData(prev => [...data.data])
             }if(page > 0){
                setData(prev => [...prev, ...data.data])
              
             }
           
         }else {
             setData((prev)=>[...data.data])
         }
 
  
       })
     .then(setIsLoading(false))
 }

 const readSingel = (API)=>{
     setSingelLoading(true)
     
     fetch(API)
     .then((res)=>res.json())
     .then((data)=>{
         setSingel((prev)=>[...data.data])    
     })
     .then(setSingelLoading(false))
 

 }

useEffect(()=>{
    
    
readSingel(SINGEL_API+num)
},[num])



function HideItemId(e){
    setNum(e.target.value)

    return setShow(true)  
}

function ShownItemId(){
    return setShow(false)  
}


//search handel//
function handelChange(e){
    const searchValue = e.target.value
        SetValue( searchValue)
        readData(SEARCH_API+searchValue)

        if (!searchValue){
        SetValue(null)
        setData([])
        setPage((prev)=>prev  * 0)
        readData(LISTED_API) 
        }

}
//Listen to Scroll //
useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);


//Set True if reach Buttom//
function handleScroll(){
        if ((window.innerHeight + window.scrollY) >= document.body.offsetHeight) {
            setIsBottom(true)
         }else{
             setIsBottom(false)
         }
}

//close Singel if Click esc
useEffect(() => {
    const close = (e) => {
      if(e.keyCode === 27){
       ShownItemId()
      }
    }
    window.addEventListener('keydown', close)
  return () => window.removeEventListener('keydown', close)
},[])

    
return(<div>

<header>

   <input style={style} type="text" name="name" placeholder="Search..." onChange={(e)=>{handelChange(e)}} />
   
 

</header>

<singelAnime/>

<div className="grid-container">

{isLoading ? 'Loading...' : data.map(e=>
      <Card 
      key={e.id}
     id={e.id}
    name={e.attributes.canonicalTitle}
    img={e.attributes.posterImage.small}
    description={e.attributes.description}
    showOn={ShownItemId} 
    showOff={HideItemId} 

    />)}

</div>


</div>)
}
    export default Movies
