import React from 'react'

function Card(props){

    const style = {
        display:"grid" ,
    }


    return(
    <div style={style}>
    <h2>{props.name}</h2>
    <img src={props.img} alt="Anime Poster"/>
    <span>{props.description}</span>
    </div>

    )
}

export default Card
