import React,{useState, useEffect} from 'react'
import Card from './cards'
import SearchAnime from './SearchAnime'
import {useHistory  } from "react-router-dom";
import Skeleton, { SkeletonTheme } from 'react-loading-skeleton';


function Movies(){

const [data , setData] = useState([])
const [isLoading , setIsLoading] = useState(true)
const [isBottom, setIsBottom] = useState(true);
const [page , setPage] = useState(0)


const LISTED_API= `https://kitsu.io/api/edge/anime?page[limit]=10&page[offset]=${page}`


useEffect(() => {
    if (isBottom) { 
         setPage((prev)=>prev + 20)
        readData(LISTED_API)
    }
  }, [isBottom]); 

//FEATCHING API DATA , SET DATA , Set Loading //

const readData = (API)=> {
    setIsLoading(true)
    fetch(API)
     .then((res)=>res.json())
     .then((data)=>{
             if(page === 0){
                setData(prev => [...data.data])
             }if(page > 0){
                setData(prev => [...prev, ...data.data])         
             }     
         
 
       })
   .then(()=>{setIsLoading(false)})
 }

//Listen to Scroll //

useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);


//Set True if reach Buttom//

function handleScroll(){
        if ((window.innerHeight + window.scrollY) >= document.body.offsetHeight-100) {
            setIsBottom(true)
         }else{
             setIsBottom(false)
         }
}

let history = useHistory()
function handleClick(e) {
    history.push("/"+e.target.value);
  }
  


return(<div>


<div>

<div>

{isLoading ? <div>
  <SkeletonTheme color="#202020" highlightColor="#444">
  <div  style={{display:"grid" , gridTemplateColumns:"1fr 1fr 1fr 1fr" , margin: "2%"}}>
  <h1>
    <Skeleton width="70%" ></Skeleton>
    <Skeleton width="300px" height="450PX "></Skeleton>
  </h1>
  <h1>
    <Skeleton  width="70%" ></Skeleton>
    <Skeleton width="300px" height="450PX "></Skeleton>
  </h1>
  <h1>
    <Skeleton width="70%" ></Skeleton>
    <Skeleton width="300px" height="450PX "></Skeleton>
  </h1>
  <h1>
    <Skeleton width="70%" ></Skeleton>
    <Skeleton width="300px" height="450PX "></Skeleton>
  </h1>
  <h1>
    <Skeleton width="70%" ></Skeleton>
    <Skeleton width="300px" height="450PX "></Skeleton>
  </h1>
  <h1>
    <Skeleton  width="70%" ></Skeleton>
    <Skeleton width="300px" height="450PX "></Skeleton>
  </h1>
  <h1>
    <Skeleton width="70%" ></Skeleton>
    <Skeleton width="300px" height="450PX "></Skeleton>
  </h1>
  <h1>
    <Skeleton width="70%" ></Skeleton>
    <Skeleton width="300px" height="450PX "></Skeleton>
  </h1>
  </div>

</SkeletonTheme>
</div>
: <div  className="grid-container">
{data.map((e)=>
      <Card 
      key={e.id}
      star={e.attributes.averageRating}
     id={e.id}
    name={e.attributes.canonicalTitle}
    img={e.attributes.posterImage.small}
    description={e.attributes.description}
    showOff={handleClick}
    />
    
    )}

</div>}

</div>
</div>

</div>)
}
    export default Movies
