import ActorCard from './ActorCard'

function AllActors(props){
const [data , setData]= useState({data:""})

const API = `https://kitsu.io/api/edge/anime/${props.num}/anime-staff`

const readData = (API)=>{
    fetch(API)
    .then((res)=>res.json())
    .then((data)=>{
        console.log(data)
        setData(prev=> ({...data.data}))
            }) 

}

useEffect(()=>{
    setTimeout(() => {
        readData(API)
    }, 1000);
    },[props.num])


console.log(data)
return !data.data ? "err" : data.data.map((e)=>{
   return <ActorCard id={e.id}/>
})

}

