import React from 'react'
import CardButtton from './cardButton'

import { useHistory } from "react-router-dom";
function Card(props){

    const maxLenght = 247
const url = "https://media.kitsu.io/anime/poster_images/10/small.jpg?1597696803"
    const style = {
        position: "relative" ,
        backgroundImage: "url(" + props.img + ")",
        height:"450px",
        backgroundSize:"cover",
        backgroundRepeat:"no-repeat",
    }

const moreInfo = {
    position:"absolute" ,
    bottom : "4%" ,
    left: "43.3%"
}

function HomeButton() {
  let history = useHistory();

  function handleClick() {
    history.push("/home");
  }

  return (
    <button type="button" onClick={handleClick}>
      Go home
    </button>
  );
}

    return(
    <div  name={props.id} style={style} >
    <div style={{ padding:"0%"}}>
     
    </div>
    <div>
    <h2 style={{backgroundColor:"#000000c4" , color:"white" , textAlign:"center" , margin:"0% auto" , padding:"5% 0%"}}>{props.name}</h2>
    </div>
    <div>
    <img/>

    </div>
  
    <div className="card-description">
    {props.description.length > maxLenght ? props.description.slice(0, maxLenght): null}



    <CardButtton
         value={props.id}
        onClick={props.showOff}
    />
    </div>
    </div>

    )
}

export default Card
