import React,{useState, useEffect} from 'react'
import Card from './cards'


function Movies(){

const [data , setData] = useState([])
const [isLoading , setIsLoading] = useState(false)

const LISTED_API= "https://kitsu.io/api/edge/trending/anime"

useEffect(()=>{
    fetch(LISTED_API)
    .then((res)=>res.json())
    .then((bata)=>{
        setData(bata.results)
    })
},[])

return(
    isLoading ? 'Loading...' : data.data.map(e=>
      <Card key={e.id} id={e.id} name={e.attributes.canonicalTitle} img={e.attributes.posterImage.small} description={e.attributes.description}/>
     
        )
       
    )};

export default Movies
