import React  from 'react'
import './App.css';
import Movies from './components/AnimeHouse'
import { Route, useParams , Switch , useLocation} from 'react-router-dom';
import SingelAnime from './components/SingelAnime';
import { useTransition, animated } from 'react-spring'
import NavBar from './components/Navbar'
import Home from './components/HomePage';
import TrendingAnime from './components/TrendingAnime';
import AnimeCarusel from './components/AnimeCarusel';
import Footer from './components/footer';
import SearchAnime from './components/SearchAnime';

function App() {


  const location = useLocation()
  const transitions = useTransition(location, location => location.pathname, {
    from: { opacity: 0, transform: 'translate3d(100%,0,0)' },
    enter: { opacity: 1, transform: 'translate3d(0%,0,0)' },
    leave: { opacity: 0, transform: 'translate3d(-50%,0,0)' },
  })

const Search = (props)=>{
return <SearchAnime/>
}


 const SingelAnimel= (props)=>{
  let { id } = useParams();
  console.log(props)
    return(
    <div className="singelAnime"> 
    <SingelAnime cardNumber={id}/>
    <div style={{background:"#141414" ,padding: "2% 50px"}}>
    <h1 style={{textAlign:"center"}}  >Tranding anime</h1>
      <TrendingAnime/>
      <h1 style={{textAlign:"center" ,  margin: "0" ,padding: "2%"}}>More Anime</h1>
    <AnimeCarusel/> 
<div style={{height:"50px"}}>

</div>
    </div>

    </div>) 
 }

 const AllMovies= (props)=>{
   return  <Movies/>

 }


  return (
    <div>

    <NavBar></NavBar> 

    <div style={{paddingLeft:"80px"}}>
    <Route exact path="/search" component={Search}/>

    <div>
  {transitions.map(({ item, props, key }) =>{
  return (
    <animated.div key={key} style={props}>
    <Switch location={item[0]}> 
    <Route exact path="/movies" component={AllMovies}/>
    <Route  path="/:id" component={SingelAnimel} />

     </Switch> 
    </animated.div>
)})}
<Footer/>
</div>
 </div>
    </div>


  );
}

export default App;
