import React from 'react'

function Card(props){

    const maxLenght = 247

    const style = {
        position: "relative"
    }

const moreInfo = {
    position:"absolute" ,
    bottom : "4%" ,
    left: "43.3%"

}



    return(
    <div  name={props.id} style={style} >
    <div>
     
    </div>
    <h2>{props.name}</h2>
    <div>
    <img style={{width:"300px",height:"300px"}}src={props.img}  alt="Anime Poster"/>

    </div>
  
    <div className="card-description">
    {props.description.length > maxLenght ? props.description.slice(0, maxLenght): null}
    <button  value={props.id} onClick={props.showOff}   className="btn-Show"></button>
    </div>
    </div>

    )
}

export default Card
