import React,{useState} from 'react'
import './App.css';
import Movies from './components/AnimeHouse'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useParams
} from "react-router-dom";
import SingelAnime from './components/SingelAnime';

function App() {

 const SingelAnimel= (props)=>{
  let { slug } = useParams();
    return  <SingelAnime cardNumber={slug}/>
 }


 function Episodes() {
  console.log(props.cardNumber)
    return <SingelEpisode num={props.cardNumber}></SingelEpisode>
  }

  function Characters() {
    return <div>
        <h1>Characters</h1>
        <Character num={props.cardNumber}></Character>
    </div>;
  }

  function Actors() {
    return <h2> Actors</h2>;
  }


  return (
    <div className="dis">


<div className="grid-container">
<Router>
      <Route path="/" component={Movies}/>
    </Router>
    <Router>
      <Route path="/:slug" component={SingelAnimel}/>
    </Router>
    <Router>
      <Route path="/:slug/episode" component={Episodes}/>
    </Router>
</div>

    </div>

  );
}

export default App;
