import React, {useState , useEffect} from 'react'
import TrendingCard from './TrendingCard'
import Slider from "react-slick";
function TrendingAnime(){



    const [Tranding , setTranding] = useState([])
    const [Loading , SetLoading] = useState(false)
    const API = "https://kitsu.io/api/edge/trending/anime"

    const readTrending = (API)=>{
        fetch(API)
        .then((res)=>res.json())
        .then((data)=>{
            setTranding(prev=> (data.data))})
    }

    useEffect(()=>{
        readTrending(API)
        setTimeout(() => {
    SetLoading(true)
        }, 2000);
        console.log(Tranding)
        },[])


    var settings = {
      dots: true,
      infinite: false,
      speed: 500,
      slidesToShow: 4,
      slidesToScroll: 4,
      initialSlide: 0,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
    
  

    return (Loading ?
        <div>
        <h2> Responsive </h2>
        <Slider {...settings}>
          <div>
            <h3>1</h3>
          </div>
          <div>
            <h3>2</h3>
          </div>
          <div>
            <h3>3</h3>
          </div>
          <div>
            <h3>4</h3>
          </div>
          <div>
            <h3>5</h3>
          </div>
          <div>
            <h3>6</h3>
          </div>
          <div>
            <h3>7</h3>
          </div>
          <div>
          <TrendingCard data={Tranding}/>
          </div>
        </Slider>
      </div>
        
       
        :  <h1>No</h1> )
}

export default TrendingAnime
