import React ,{ useState , useEffect} from 'react'

import SearchAnime from './SearchAnime'


function Next(props){


    const [data , setData] = useState({
        Loading : false,
        isBottom:false,
        page:0
    })


    const LISTED_API= `https://kitsu.io/api/edge/anime?page[limit]=10&page[offset]=${data.page}`


    useEffect(() => {
        if (data.isBottom) { 
             data.page+20
            readData(LISTED_API)
        }
      }, [data.isBottom]); 

    const readData = (API)=>{
        fetch(API)
        .then((res)=>res.json())
        .then((data)=>{
            setSingel((prev)=>[...data.data])    
        })
    
       
    }


    return(
        <div>
        <SearchAnime/>
        </div>
    )
}

export default Next
