import React from 'react'

function Card(props){

    const maxLenght = 247

    const style = {
        position: "relative"
    }

const moreInfo = {
    position:"absolute" ,
    bottom : "4%" ,
    left: "43.3%"

}
const lessInfo = {
    width:"100%",

    

}



    return(
    <div  name={props.id} style={style} >
    <div>
        <h2>{props.name}</h2>
    </div>
    <img src={props.img} style={lessInfo} alt="Anime Poster"/>
    <div className="card-description">
    {props.description.length > maxLenght ? props.description.slice(0, maxLenght): null}
    <button  value={props.id} onClick={props.showOff}   className="btn-Show"></button>
    </div>
    </div>

    )
}

export default Card
