import React from 'react'

function Card(props){

    const maxLenght = 247

    const style = {
        position: "relative"
    }

const moreInfo = {
    position:"absolute" ,
    bottom : "4%" ,
    left: "33.3%"

}
    function onClick(e , data){
        console.log(e.target.value)
    }


    return(
    <div style={style} > 
    <h2>{props.name}</h2>
    <img src={props.img} alt="Anime Poster"/>
    <div className="card-description">
    {props.description}
    <button onClick={((e)=>{onClick(e)})} value={props.id} style = {moreInfo}>"More Info"</button>
    </div>
    </div>

    )
}

export default Card
