import React , {useState , useEffect} from 'react'


function ActorCard(props){

    

    const [Actor , SetActor] = useState([])
    const SINGEL_ACTOR_API = `https://kitsu.io/api/edge/staff/${props.id}/person`


    useEffect(()=>{ 

        fetch(SINGEL_ACTOR_API)
        .then((res)=>res.json())
        .then((data)=>{
            SetActor(prev=> ({ ...data.data,...prev.data}))
            }) 
        }
        ,[])



    return(!Actor.id ? "...Loading": <div>
        <h1>{Actor.name}</h1>
    </div>  )
}

export default ActorCard
